mapper.delete:
delete from sys_menu
where menu_id in

  #{id}

mapper.select:
select menu_id from sys_menu
where parent_id in

  #{parentId}

select id, menu_id, name, parent_id, url, perms, type, icon, order_num, create_by, create_time, last_update_by, last_update_time, del_flag, menu_code, menu_belong, menu_sys from sys_menu where menu_code in
#{item}
and del_flag = 0 and menu_sys = #{menuSys,jdbcType=VARCHAR} order by order_num asc
