mapper.select:
select count(name)
from sys_user
where name = #{name,jdbcType=VARCHAR}

select count(nick_name)
from sys_user
where nick_name = #{nickName,jdbcType=VARCHAR}

select id, user_id, name, nick_name, avatar, email, mobile, status, is_allow,is_allow_work,
dept_id, create_by, create_time, last_update_by, last_update_time, del_flag, user_number_id, sex
from sys_user where 1=1

  and name like CONCAT('%', #{name, jdbcType=VARCHAR}, '%')


  and nick_name like CONCAT('%', #{nickName, jdbcType=VARCHAR}, '%')


  and email like CONCAT('%', #{email, jdbcType=VARCHAR}, '%')


  and mobile like CONCAT('%', #{mobile, jdbcType=VARCHAR}, '%')


and status = #{status,jdbcType=INTEGER}


  and dept_id = #{deptId,jdbcType=VARCHAR}


  and del_flag = #{delFlag,jdbcType=INTEGER}


  and sex = #{sex,jdbcType=INTEGER}


  and is_allow = #{isAllow,jdbcType=INTEGER}


  and is_allow_work = #{isAllowWork,jdbcType=INTEGER}


  and user_number_id like CONCAT('%', #{userNumberId, jdbcType=VARCHAR}, '%')

order by create_time desc
